import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import LoginScreen from '../screens/LoginScreen';
import RegisterScreen from '../screens/RegisterScreen';
import HomeScreen from '../screens/HomeScreen';
import CameraConnectScreen from '../screens/CameraConnectScreen';
import CameraDetailsScreen from '../screens/CameraDetailsScreen';

const Tab = createBottomTabNavigator();

const Navigation = () => {
  return (
    <NavigationContainer>
      <Tab.Navigator
          options={{
            tabBarVisible: false, // Oculta la barra de navegaci贸n en LoginScreen
          }}>
        <Tab.Screen
          name="Login"
          component={LoginScreen}
          options={{
            tabBarVisible: false, // Oculta la barra de navegaci贸n en LoginScreen
          }}
        />
        <Tab.Screen
          name="Register"
          component={RegisterScreen}
          options={{
            tabBarVisible: false, // Oculta la barra de navegaci贸n en RegisterScreen
          }}
        />
        <Tab.Screen
          name="Main"
          component={MainScreenStack}
          options={{
            tabBarVisible: false, // Oculta la barra de navegaci贸n en RegisterScreen
          }}
        />
      </Tab.Navigator>
    </NavigationContainer>
  );
};

const MainScreenStack = () => {
  return (
    <Tab.Navigator>
      <Tab.Screen
        name="Home"
        component={HomeScreen}
      />
      <Tab.Screen
        name="Connect"
        component={CameraConnectScreen}
      />
      <Tab.Screen
        name="CameraDetails"
        component={CameraDetailsScreen}
      />
    </Tab.Navigator>
  );
};

export default Navigation;
