import React, { useContext } from 'react'
import { View, Text, TextInput, StyleSheet, Pressable, TouchableOpacity, Keyboard, Alert } from 'react-native'
import { FontAwesome, MaterialCommunityIcons } from '@expo/vector-icons'
import { Icon, CheckBox } from 'react-native-elements'
import Constants from 'expo-constants'
import { useFormik } from 'formik'
import * as yup from 'yup'

import { useTogglePasswordVisibility } from '../hooks/useTogglePassVisibility'
import { COLORS } from '../constants'
import { AuthContext } from '../context/AuthContext'
import { StatusBar } from 'expo-status-bar'
import { useState } from 'react'

const validationSchema = yup.object().shape({
  username: yup.string().required('El usuario es requerido'),
  password: yup.string().required('La contraseña es requerida')
});

export default function LoginScreen () {
    const { login } = useContext(AuthContext)
    const { passwordVisibility, rightIcon, handlePasswordVisibility } = useTogglePasswordVisibility()
    const [isSelected, setSelection] = useState(true)
    const toggleCheckbox = () => setSelection(!isSelected)

  
  const handleRegisterPress = () => {
    navigation.navigate('Register');
  };

  return (
    <View style={styles.container}>
      <View style={styles.final}>
        <TouchableOpacity style={styles.registerButton} onPress={handleRegisterPress}>
            <Text style={styles.registerButtonText}>Crear una cuenta</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      paddingTop: Constants.statusBarHeight,
      backgroundColor: COLORS.WHITE
    },
    form: {
      width: '90%',
      marginTop: 30
    },
    title: {
      fontSize: 30,
      fontWeight: '900',
      marginTop: '20%',
      marginBottom: 24
    },
    inputContainer: {
      backgroundColor: COLORS.GRAY,
      flexDirection: 'row',
      borderRadius: 4,
      borderWidth: 0
    },
    iconEyeButton: {
      justifyContent: 'center',
      alignItems: 'center'
    },
    icono: {
      padding: 10
    },
    input: {
      flex: 1,
      height: 44,
      backgroundColor: COLORS.GRAY,
      borderRadius: 4
    },
    containerRecuerdame: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginTop: 20
    },
    row: {
      flexDirection: 'row',
      alignItems: 'center'
    },
    flexRow: {
      flexDirection: 'row'
    },
    textOlvidaste: {
      color: COLORS.ORANGE
    },
    separatorContainer: {
      marginTop: 10,
      flexDirection: 'row',
      justifyContent: 'center',
      marginBottom: 16
    },
    separatorText: {
      marginHorizontal: 8,
      fontSize: 14,
      color: COLORS.GRAY_EXTRA_SOFT
    },
    biniciar: {
      marginTop: 20,
      backgroundColor: COLORS.ORANGE,
      width: '100%',
      height: 48,
      borderRadius: 8,
      justifyContent: 'center',
      alignItems: 'center',
      marginBottom: 20,
      shadowColor: COLORS.ORANGE,
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 10
    },
    searchIcon: {
      padding: 10
    },
    buttonContainer: {
      flexDirection: 'row',
      justifyContent: 'center',
      marginTop: '3%'
  
    },
    buttonWrapperg: {
      backgroundColor: '#FEF1F1FF',
      alignItems: 'center',
      padding: '2%',
      width: '18%',
      marginRight: 10,
      borderRadius: 20
    },
    buttonWrappera: {
      backgroundColor: '#F3F4F6FF',
      alignItems: 'center',
      padding: '2%',
      width: '18%',
      marginRight: 10,
      borderRadius: 20
    },
    buttonWrapperf: {
      backgroundColor: '#F3F6FBFF',
      alignItems: 'center',
      padding: '2%',
      width: '18%',
      marginRight: 10,
      borderRadius: 20
    },
    final: {
      flexDirection: 'row',
      justifyContent: 'center',
      marginBottom: 16,
      marginTop: 70
    },
    registrar: {
      fontWeight: 'bold',
      color: COLORS.ORANGE
    },
    errorContainer: {
      height: 50,
      marginTop: 10,
      alignSelf: 'flex-start',
      marginLeft: 20
    },
    error: {
      color: COLORS.DANGER
    }
  })
