import React, { useState, useContext } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { AuthContext } from '../context/AuthContext';
import { Formik } from 'formik';
import { COLORS } from '../constants';

const LoginScreen = ({ navigation }) => {
  const { login } = useContext(AuthContext);

  const handleLogin = async (values) => {
    try {
      // Lógica de autenticación aquí
      const token = 'tokenEjemplo';

      await login(token);
      navigation.navigate('Home');
    } catch (error) {
      // Manejar errores de autenticación aquí
    }
  };

  const handleRegisterPress = () => {
    try {
        navigation.navigate('Register');
      } catch (error) {
        // Manejar errores de autenticación aquí
      }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>¡Hola de nuevo!</Text>
      <Formik
        initialValues={{ email: '', password: '' }}
        onSubmit={values => handleLogin(values)} // Llama a handleLogin cuando se envía el formulario
      >
        {({ handleChange, handleSubmit, values }) => (
          <View>
            <TextInput
              style={styles.input}
              placeholder="Correo Electrónico"
              value={values.email}
              onChangeText={handleChange('email')}
              placeholderTextColor={COLORS.LIGHT_GRAY}
            />
            <TextInput
              style={styles.input}
              placeholder="Contraseña"
              secureTextEntry
              value={values.password}
              onChangeText={handleChange('password')}
              placeholderTextColor={COLORS.LIGHT_GRAY}
            />
            <TouchableOpacity style={styles.loginButton} onPress={handleSubmit}>
              <Text style={styles.buttonText}>Iniciar Sesión</Text>
            </TouchableOpacity>
          </View>
        )}
      </Formik>
      <TouchableOpacity style={styles.registerButton} onPress={handleRegisterPress}>
        <Text style={styles.registerButtonText}>Crear una cuenta</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: COLORS.WHITE,
  },
  title: {
    fontSize: 32,
    marginBottom: 20,
    color: COLORS.PRIMARY,
    fontWeight: 'bold',
  },
  input: {
    width: '80%',
    height: 50,
    borderColor: COLORS.LIGHT_GRAY,
    borderWidth: 1,
    marginBottom: 20,
    paddingHorizontal: 20,
    color: COLORS.DARK_GRAY,
    borderRadius: 10,
  },
  loginButton: {
    borderColor: COLORS.PRIMARY,
    borderWidth: 2,
    backgroundColor: COLORS.PRIMARY,
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 25,
    marginBottom: 10,
  },
  buttonText: {
    color: COLORS.PRIMARY,
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  registerButton: {
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 25,
  },
  registerButtonText: {
    color: COLORS.PRIMARY,
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default LoginScreen;
