import React, { useState, useEffect } from "react";
import Webcam from "react-webcam";
import { View, Button, Select, MenuItem } from "react-native";

const CameraConnectScreen = () => {
  const [connected, setConnected] = useState(false);
  const [devices, setDevices] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState(null);

  useEffect(() => {
    // Obtener la lista de dispositivos de entrada disponibles
    navigator.mediaDevices.enumerateDevices().then((devices) => {
      const cameras = devices.filter((device) => device.kind === "videoinput");
      setDevices(cameras);
    });
  }, []);

  const handleConnect = () => {
    // Conectar a la c치mara seleccionada
    setConnected(true);
  };

  return (
    <View>
      {!connected ? (
        <View>
          <Select value={selectedDevice} onChange={(e) => setSelectedDevice(e.target.value)}>
            <MenuItem value={null}>Seleccionar c치mara</MenuItem>
            {devices.map((device) => (
              <MenuItem key={device.deviceId} value={device.deviceId}>
                {device.label || `C치mara ${device.deviceId}`}
              </MenuItem>
            ))}
          </Select>
          <Button
            title="Conectar a la c치mara"
            onClick={handleConnect}
            disabled={!selectedDevice}
          />
        </View>
      ) : (
        <View>
          <Webcam deviceId={selectedDevice} />
        </View>
      )}
    </View>
  );
};

export default CameraConnectScreen;
