import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import LoginScreen from '../screens/LoginScreen';
import RegisterScreen from '../screens/RegisterScreen';
import HomeScreen from '../screens/HomeScreen';
import CameraConnectScreen from '../screens/CameraConnectScreen';
import CameraDetailsScreen from '../screens/CameraDetailsScreen';

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

const Navigation = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Login" headerMode="none">
        <Stack.Screen name="Home" component={MainScreenStack} />
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Register" component={RegisterScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const MainScreenStack = () => {
  return (
    <Tab.Navigator>
      <Tab.Screen
        name="Home"
        component={HomeScreen}
      />
      <Tab.Screen
        name="Connect"
        component={CameraConnectScreen}
      />
      <Tab.Screen
        name="CameraDetails"
        component={CameraDetailsScreen}
      />
    </Tab.Navigator>
  );
};

const MainStack = () => {
  const [registerUser, setRegisterUser] = useState(false)
  return (
    registerUser
      ? <RegisterScreen setRegisterUser={setRegisterUser} />
      : <LoginScreen setRegisterUser={setRegisterUser} />
  )
}

export default Navigation;
