import React, { useState } from "react";
import { View, TextInput, Button } from "react-native";
import Webcam from "react-webcam";

const CameraConnectScreen = () => {
  const [cameras, setCameras] = useState([]);
  const [camera, setCamera] = useState(null);
  const [cameraId, setCameraId] = useState(null);

  const handleConnect = () => {
    // Conectar a la cámara seleccionada
    setCamera(cameras[cameraId]);
  };

  const handleShowCamera = () => {
    // Mostrar la cámara en tiempo real
    const webcam = useRef(null);
    webcam.current.play();
  };

  return (
    <View>
      <Webcam
        ref={camera}
        style={{
          width: 400,
          height: 300,
        }}
      />
      <List
        data={cameras}
        renderItem={({ item }) => (
          <ListItem
            key={item.id}
            title={item.name}
            onPress={() => setCameraId(item.id)}
          />
        )}
      />
      <Button
        title="Conectar"
        onPress={handleConnect}
      />
      <Button
        title="Mostrar cámara"
        onPress={handleShowCamera}
      />
    </View>
  );
};

export default CameraConnectScreen;
