import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack'; // Importa el createStackNavigator
import HomeScreen from '../screens/HomeScreen';
import CameraConnectScreen from '../Screens/CameraConnectScreen';
import CameraDetailsScreen from '../Screens/CameraDetailsScreen';
import LoginScreen from '../screens/LoginScreen'; // Importa tu LoginScreen
import RegisterScreen from '../screens/RegisterScreen'; // Importa tu RegisterScreen

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator(); // Crea el StackNavigator

const MainTabNavigator = () => (
  <Tab.Navigator initialRouteName="Home">
    <Tab.Screen name="Home" component={HomeScreen} />
    <Tab.Screen name="Connect" component={CameraConnectScreen} />
    <Tab.Screen name="CameraDetails" component={CameraDetailsScreen} />
  </Tab.Navigator>
);

const Navigation = () => {
  return (
    <Stack.Navigator initialRouteName="Login">
      <Stack.Screen name="Login" component={LoginScreen} options={{ headerShown: false }} />
      <Stack.Screen name="Register" component={RegisterScreen} options={{ headerShown: false }} />
      <Stack.Screen name="MainTab" component={MainTabNavigator} options={{ headerShown: false }} />
    </Stack.Navigator>
  );
};

export default Navigation;
