import React, { useState, useEffect, useRef } from "react";
import Webcam from "react-webcam";
import { FormControl, Select, MenuItem, Button } from "@mui/material"; // Importa los componentes de tu elección, como @mui/material

import io from "socket.io-client";

const CameraConnectScreen = () => {
  const [connected, setConnected] = useState(false);
  const [devices, setDevices] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [socket, setSocket] = useState(null);

  const webcamRef = useRef(null); // Referencia a la webcam

  useEffect(() => {
    // Obtener la lista de dispositivos de entrada disponibles
    navigator.mediaDevices.enumerateDevices().then((devices) => {
      const cameras = devices.filter((device) => device.kind === "videoinput");
      setDevices(cameras);
    });
  }, []);

  const handleConnect = () => {
    // Conectar a la cámara seleccionada
    setConnected(true);

    // Establecer la conexión WebSocket
    const newSocket = io("http://localhost:5000"); // Cambia la URL a tu backend
    setSocket(newSocket);
  };

  useEffect(() => {
    if (socket) {
      // Escuchar eventos desde el backend
      socket.on("connected", (data) => {
        console.log("Connected to server:", data);
        // Enviar imagen automáticamente al conectarse
        handleSendImage();
      });

      socket.on("prediction", (prediction) => {
        console.log("Prediction received:", prediction);
        // Aquí puedes mostrar la notificación o realizar otras acciones según la predicción
      });

      socket.on("error", (error) => {
        console.error("Error received:", error);
      });
    }
  }, [socket]);

  const handleSendImage = async () => {
    console.log("Sending image...");
    if (socket && selectedDevice && webcamRef.current) {
      // Capturar la imagen de la webcam
      const imageSrc = webcamRef.current.getScreenshot();

      // Convertir la imagen a formato Blob
      const res = await fetch(imageSrc);
      const blob = await res.blob();

      // Crear un objeto File a partir del Blob
      const imageFile = new File([blob], "image.png", { type: "image/png" });

      // Enviar la imagen al backend a través del socket
      socket.emit("image", { image: imageFile });
    }
  };

  return (
    <div>
      {!connected ? (
        <div>
          <FormControl>
            <Select
              value={selectedDevice}
              onChange={(e) => setSelectedDevice(e.target.value)}
            >
              <MenuItem value="">Seleccionar cámara</MenuItem>
              {devices.map((device) => (
                <MenuItem
                  key={device.deviceId}
                  value={device.deviceId}
                >
                  {device.label || `Cámara ${device.deviceId}`}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Button
            variant="contained"
            color="primary"
            onClick={handleConnect}
            disabled={!selectedDevice}
          >
            Conectar a la cámara
          </Button>
        </div>
      ) : (
        <div>
          <Webcam audio={false} videoConstraints={{ deviceId: selectedDevice }} ref={webcamRef} />
        </div>
      )}
    </div>
  );
};

export default CameraConnectScreen;
