import React, { useEffect } from 'react'
import * as NavigationBar from 'expo-navigation-bar'

import { AuthProvider } from './src/context/AuthContext'
import { NavigationContainer } from '@react-navigation/native'; // Importa NavigationContainer
import MainTabNavigator from './src/navigation/Navigation' // Importa el MainTabNavigator
import { StatusBar } from 'expo-status-bar'

export default function App () {
  useEffect(() => {
    const changeStatusBarColor = async () => {
      NavigationBar.setBackgroundColorAsync('#FFFFFF')
      NavigationBar.setButtonStyleAsync('dark')
    }
    changeStatusBarColor()
  }, [])

  return (
    <AuthProvider>
      <StatusBar style='dark' backgroundColor='white' />
      <NavigationContainer>
        <AuthStack /> {/* Renderiza el MainTabNavigator aqu√≠ */}
      </NavigationContainer>
    </AuthProvider>
  )
}
