import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { AuthProvider } from './src/context/AuthContext'; // Importa tu contexto de autenticaciÃ³n
import HomeScreen from './src/screens/HomeScreen';
import CameraConnectScreen from './src/screens/CameraConnectScreen';
import CameraDetailsScreen from './src/screens/CameraDetailsScreen';
import LoginScreen from './src/screens/LoginScreen'; // Importa tu pantalla de login
import RegisterScreen from './src/screens/RegisterScreen'; // Importa tu pantalla de registro

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <AuthProvider>
        <Stack.Navigator initialRouteName="Auth">
          <Stack.Screen name="Auth" component={AuthNavigator} />
          <Stack.Screen name="MainApp" component={MainAppNavigator} />
        </Stack.Navigator>
      </AuthProvider>
    </NavigationContainer>
  );
};

const AuthNavigator = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Login" component={LoginScreen} />
      <Stack.Screen name="Register" component={RegisterScreen} />
    </Stack.Navigator>
  );
};

const MainAppNavigator = () => {
  return (
    <Tab.Navigator>
      <Tab.Screen name="Home" component={HomeScreen} />
      <Tab.Screen name="Connect" component={CameraConnectScreen} />
      <Tab.Screen name="CameraDetails" component={CameraDetailsScreen} />
    </Tab.Navigator>
  );
};

export default App;
