import React, { useContext } from 'react'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import LoginScreen from '../screens/LoginScreen';
import RegisterScreen from '../screens/RegisterScreen';
import HomeScreen from '../screens/HomeScreen';
import CameraConnectScreen from '../screens/CameraConnectScreen';
import CameraDetailsScreen from '../screens/CameraDetailsScreen';
import MainStack from './MainStack'
import { AuthContext } from '../context/AuthContext'
import { useNotificationContext } from '../context/NotificationContext'

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

const Navigation = () => {
  const { isLoading, userToken } = useContext(AuthContext)
  const { notification, setNotification } = useNotificationContext()

  return (
    <NavigationContainer>
    {userToken !== null ? <AuthStack /> : <MainStack />}
      {notification && (
        <CustomAlert
          notification={notification}
          onClose={() => setNotification('')}
        />,
        <Stack.Navigator initialRouteName="Login" headerMode="none">
          <Stack.Screen name="Home" component={MainScreenStack} />
          <Stack.Screen name="Login" component={LoginScreen} />
          <Stack.Screen name="Register" component={RegisterScreen} />
        </Stack.Navigator>
      )}
    </NavigationContainer>
  );
};

const MainScreenStack = () => {
  return (
    <Tab.Navigator>
      <Tab.Screen
        name="Home"
        component={HomeScreen}
      />
      <Tab.Screen
        name="Connect"
        component={CameraConnectScreen}
      />
      <Tab.Screen
        name="CameraDetails"
        component={CameraDetailsScreen}
      />
    </Tab.Navigator>
  );
};

export default Navigation;
