import React, { useState, useEffect, useRef } from "react";
import Webcam from "react-webcam";
import { View, Picker } from "react-native"; // Cambia la importación a "react-native" en lugar de "react-dom"
import io from "socket.io-client";

const CameraConnectScreen = () => {
  const [connected, setConnected] = useState(false);
  const [devices, setDevices] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [socket, setSocket] = useState(null);

  const webcamRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then((devices) => {
      const cameras = devices.filter((device) => device.kind === "videoinput");
      setDevices(cameras);
    });
  }, []);

  useEffect(() => {
    if (socket) {
      // Establecer un temporizador para enviar imágenes automáticamente
      const imageSendingInterval = setInterval(() => {
        handleSendImage();
      }, 5000); // Envía una imagen cada 5 segundos

      return () => {
        clearInterval(imageSendingInterval); // Limpia el temporizador al desmontar el componente
      };
    }
  }, [socket]);

  const handleConnect = () => {
    setConnected(true);
    const newSocket = io("http://localhost:5000");
    setSocket(newSocket);
  };

  const handleSendImage = () => {
    if (socket && selectedDevice && webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();

      const formData = new FormData();
      formData.append("image", {
        uri: imageSrc,
        name: "image.png",
        type: "image/png",
      });

      socket.emit("image", { image: formData });
    }
  };

  return (
    <View>
      {!connected ? (
        <View>
          <Picker
            selectedValue={selectedDevice}
            onValueChange={(value) => setSelectedDevice(value)}
          >
            <Picker.Item label="Seleccionar cámara" value="" />
            {devices.map((device) => (
              <Picker.Item
                key={device.deviceId}
                label={device.label || `Cámara ${device.deviceId}`}
                value={device.deviceId}
              />
            ))}
          </Picker>
          <Button
            title="Conectar a la cámara"
            onPress={handleConnect}
            disabled={!selectedDevice}
          />
        </View>
      ) : (
        <View>
          <Webcam
            audio={false}
            videoConstraints={{ deviceId: selectedDevice }}
            ref={webcamRef}
          />
        </View>
      )}
    </View>
  );
};

export default CameraConnectScreen;
